package algz.model.ui;

import javax.persistence.Column;
import javax.persistence.Entity;

import core.base.BaseEntity;

@Entity
public class Component extends BaseEntity{

	private static final long serialVersionUID = 7154716932779611606L;
	
	public static Component LABEL 	= new Component(1,"Label","<span>{{$this.text}}</span>","{\"text\":\"Text\"}");
	public static Component BUTTON 	= new Component(2,"Button","<button class=\"btn {{$this.type}}\">{{$this.text}}</button>","{\"text\":\"Text\"}");
	public static Component IMAGE 	= new Component(3, "Image", "<img  style=\"width:32px\" src=\"{{$this.source}}\"\\>","{\"source\":\"data:image/png;base64,\"}");
	
	private String htmlContent;
	@Column(length=16000)
	private String jsonExemple;
	private String name;
	
	public Component(){};
	public Component(int id,String name, String htmlContent,String jsonExemple) 
	{
		setId(id);
		setHtmlContent(htmlContent);
		setName(name);
		setJsonExemple(jsonExemple);
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public String getJsonExemple() {
		return jsonExemple;
	}
	public void setJsonExemple(String jsonExemple) {
		this.jsonExemple = jsonExemple;
	}
	public String getHtmlContent() {
		return htmlContent;
	}
	public void setHtmlContent(String htmlContent) {
		this.htmlContent = htmlContent;
	}
}
